import React, { useState } from 'react'
import { useSelector } from 'react-redux';
import * as dayjs from 'dayjs';
import relativeTime from 'dayjs/plugin/relativeTime';
import 'react-dropdown/style.css';
import Pedido from './pedido/Pedido';
dayjs.extend(relativeTime);

export default function Pedidos({ setPedidoId }) {
	const [clientId, setClienteId] = useState(0);
	const pedidos = useSelector(state => state.pedidos.find((pedido) => pedido.cliente === clientId ? pedido : null))
	const clienteSeleccionado = event => {
		if (event.target.value) {
			setClienteId(event.target.value);
		}
	};
	const clientes = useSelector(state => state.clientes)
	return (
		<>
			<div>
				<select className="smallLogindrop" onChange={clienteSeleccionado}>
					<option value="" />
					{clientes.map(cliente => {
						return <option key={cliente._id} value={cliente._id} >{cliente.nickname}</option>;
					})}
				</select>
			</div>
			<div>
				{pedidos.map(pedido  => {
					return <Pedido setPedidoId={setPedidoId} items={pedido.items} pedidoId={pedido._id} key={pedido._id}/>;
				})}
			</div>
		</>
	)
}
